Scripts.OnClick:
if ( IsModifiedClick() ) then
	SpellButton_OnModifiedClick(self, button);
else
	SpellButton_OnClick(self, button);
end

if ( IsModifiedClick() ) then
	SpellButton_OnModifiedClick(self, button);
else
	SpellButton_OnClick(self, button);
end

if ( IsModifiedClick() ) then
	SpellBookCompanionButton_OnModifiedClick(self, button);
else
	SpellBookCompanionButton_OnClick(self, button);
end
PlaySound("igMainMenuOptionCheckBoxOn");

local dialog = StaticPopup_Show("UNLEARN_SKILL", self:GetParent().skillName);
if ( dialog ) then
	dialog.data = self:GetParent().skillLine;
end
